import React, { Component } from 'react';
import { EchoClient } from './jsclient/echo_grpc_web_pb';
import { Msg } from './jsclient/echo_pb';

class GrpcClient extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputText: '',
      inputImage: null,
      responseText: '',
    };
  }

  handleChangeText = (event) => {
    this.setState({ inputText: event.target.value });
  };

  handleChangeImage = (event) => {
    const file = event.target.files[0];
    this.setState({ inputImage: file });
  };

  handleSendText = () => {
    const { inputText } = this.state;
    const grpcService = new EchoClient('http://localhost:8080'); // Replace with your gRPC server URL
    grpcService.sendText(inputText, (response) => {
      this.setState({ responseText: response.getMessage() });
    });
  };

  handleSendImage = () => {
    const { inputImage } = this.state;
    const grpcService = new EchoClient('http://localhost:8080'); // Replace with your gRPC server URL
    grpcService.sendImage(inputImage, (response) => {
      // Handle the image upload response here
      // For example, you can display the image uploaded
    });
  };

  render() {
    return (
      <div>
        <h1>gRPC Client</h1>
        <div>
          <input
            type="text"
            placeholder="Enter text"
            value={this.state.inputText}
            onChange={this.handleChangeText}
          />
          <button onClick={this.handleSendText}>Send Text</button>
        </div>
        <div>
          <input type="file" onChange={this.handleChangeImage} />
          <button onClick={this.handleSendImage}>Send Image</button>
        </div>
        <div>
          <p>Response:</p>
          <p>{this.state.responseText}</p>
        </div>
      </div>
    );
  }
}

export default GrpcClient;
