// src/GrpcClient.js
import React, { Component } from 'react';
import { EchoClient } from './proto/echo_grpc_web_pb';
import { Msg } from './proto/echo_pb';

class GrpcClient extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputText: '',
      responseText: '',
    };
  }

  handleChange = (event) => {
    this.setState({ inputText: event.target.value });
  };

  handleSendRequest = () => {
    const { inputText } = this.state;

    const request = new Msg();
    request.setText(inputText);

    const client = new EchoClient('http://172.17.0.2:8080'); // Replace with your gRPC server URL
    client.echo(request, {}, (err, response) => {
      if (!err) {
        this.setState({ responseText: response.getMessage() });
      } else {
        console.error('Error:', err);
      }
    });
  };

  render() {
    return (
      <div>
        <h1>gRPC Client</h1>
        <div>
          <input
            type="text"
            placeholder="Enter text"
            value={this.state.inputText}
            onChange={this.handleChange}
          />
          <button onClick={this.handleSendRequest}>Send</button>
        </div>
        <div>
          <p>Response:</p>
          <p>{this.state.responseText}</p>
        </div>
      </div>
    );
  }
}

export default GrpcClient;
