import styled from "styled-components";
import { NavLink as Link } from "react-router-dom";

export const Nav = styled.nav`
  background: #808080; /* Default background color for the navbar */
  height: 85px;
  display: flex;
  justify-content: space-between;
  padding: 0.2rem calc((100vw - 1000px) / 2);
  z-index: 12;
  transition: background-color 0.2s ease-in-out;
`;

export const NavLink = styled(Link)`
  color: #ffffff; /* White text color */
  border: none;
  border-radius: 4px;
  padding: 0.5rem 1rem;
  text-decoration: none;
  height: 100%;
  cursor: pointer;
  transition: background-color 0.2s ease-in-out;

  &.active {
    background-color: #4d4dff; /* Different color for active link */
  }

  &:hover {
    background-color: #4d4dff; /* Change color on hover */
  }
`;

// Rest of your styled-components code...


export const NavMenu = styled.div`
display: flex;
align-items: center;
margin-right: -24px;
/* Second Nav */
/* margin-right: 24px; */
/* Third Nav */
/* width: 100vw;
white-space: nowrap; */
@media screen and (max-width: 768px) {
	display: none;
}
`;
